initializeCode:
function(display) {
  this.display = display;
  this.canvas = document.createElement('canvas');
  this.canvas.width = 280;
  this.canvas.height = 248;
  display.appendChild(this.canvas);
  this.ctx = this.canvas.getContext('2d');

  this.width = this.gameData.width;
  this.height = this.gameData.height;
  this.cellSize = this.canvas.width / this.width;

  this.worm = [
    {x: 5, y: Math.floor(this.height/2)},
    {x: 4, y: Math.floor(this.height/2)},
    {x: 3, y: Math.floor(this.height/2)}
  ];

  this.food = {x: Math.floor(this.width/2), y: Math.floor(this.height/2)};
  this.score = 0;
  this.highScore = 0;
  this.gameOver = false;
  this.consecutiveCollisions = 0;
  this.startTime = Date.now();
  this.timeAlive = 0;
  this.speed = this.gameData.initialSpeed;
  this.lastUpdateTime = 0;

  // AI decision making variables
  this.lastDirection = {x: 1, y: 0}; // Start moving right

  this.render();
}

handleInputCode:
function(button, isPressed) {
  if (!isPressed || this.gameOver) return;

  const direction = {
    up: {x: 0, y: -1},
    right: {x: 1, y: 0},
    down: {x: 0, y: 1},
    left: {x: -1, y: 0}
  };

  if (button in direction) {
    // Move the food in the pressed direction
    const newX = this.food.x + direction[button].x;
    const newY = this.food.y + direction[button].y;

    // Check if the new position is valid
    if (newX >= 0 && newX < this.width && newY >= 0 && newY < this.height) {
      this.food.x = newX;
      this.food.y = newY;
    }
  } else if (button === 'a' && this.gameOver) {
    this.restart();
  }
}

updateCode:
function(deltaTime) {
  if (this.gameOver) return;

  // Calculate time alive
  this.timeAlive = Math.floor((Date.now() - this.startTime) / 1000);

  // Only update worm position at certain intervals based on speed
  this.lastUpdateTime += deltaTime;
  if (this.lastUpdateTime < this.speed) return;
  this.lastUpdateTime = 0;

  // Move the worm AI
  this.moveWorm();

  // Update the game state
  this.render();
}

renderCode:
function() {
  // Clear canvas
  this.ctx.fillStyle = this.gameData.backgroundColor;
  this.ctx.fillRect(0, 0, this.canvas.width, this.canvas.height);

  // Draw worm
  for (let i = 0; i < this.worm.length; i++) {
    const segment = this.worm[i];

    // Head has different color than body
    this.ctx.fillStyle = i === 0 ? this.gameData.wormColor : this.gameData.wormBodyColor;

    // Draw segment (circle for head, rounded squares for body)
    if (i === 0) {
      // Head as circle
      this.ctx.beginPath();
      this.ctx.arc(
        segment.x * this.cellSize + this.cellSize/2,
        segment.y * this.cellSize + this.cellSize/2,
        this.cellSize/2,
        0,
        Math.PI * 2
      );
      this.ctx.fill();
    } else {
      // Body as rounded rectangle
      this.roundRect(
        segment.x * this.cellSize,
        segment.y * this.cellSize,
        this.cellSize,
        this.cellSize,
        this.cellSize/4
      );
    }
  }

  // Draw food
  this.ctx.fillStyle = this.gameData.foodColor;
  this.ctx.beginPath();
  this.ctx.arc(
    this.food.x * this.cellSize + this.cellSize/2,
    this.food.y * this.cellSize + this.cellSize/2,
    this.cellSize/2,
    0,
    Math.PI * 2
  );
  this.ctx.fill();

  // Draw stats
  this.ctx.fillStyle = 'rgba(0, 0, 0, 0.7)';
  this.ctx.fillRect(0, 0, this.canvas.width, 20);

  this.ctx.fillStyle = 'white';
  this.ctx.font = '10px Arial';
  this.ctx.textAlign = 'left';
  this.ctx.textBaseline = 'top';
  this.ctx.fillText(`SCORE: ${this.score}`, 10, 5);

  this.ctx.textAlign = 'center';
  this.ctx.fillText(`LENGTH: ${this.worm.length}`, this.canvas.width/2, 5);

  this.ctx.textAlign = 'right';
  this.ctx.fillText(`TIME: ${this.timeAlive}s`, this.canvas.width - 10, 5);

  // Draw game over message
  if (this.gameOver) {
    this.ctx.fillStyle = 'rgba(0, 0, 0, 0.7)';
    this.ctx.fillRect(0, 0, this.canvas.width, this.canvas.height);

    this.ctx.fillStyle = 'white';
    this.ctx.textAlign = 'center';
    this.ctx.textBaseline = 'middle';
    this.ctx.font = '16px Arial';
    this.ctx.fillText('GAME OVER', this.canvas.width/2, this.canvas.height/2 - 30);

    this.ctx.font = '12px Arial';
    this.ctx.fillText(`SCORE: ${this.score}`, this.canvas.width/2, this.canvas.height/2);
    this.ctx.fillText(`HIGH SCORE: ${this.highScore}`, this.canvas.width/2, this.canvas.height/2 + 20);
    this.ctx.fillText('PRESS A TO RESTART', this.canvas.width/2, this.canvas.height/2 + 50);
  }
}

additionalFunctions.moveWorm:
function() {
  const head = { ...this.worm[0] };
  const potentialDirections = [
    { x: 1, y: 0 },   // right
    { x: -1, y: 0 },  // left
    { x: 0, y: 1 },   // down
    { x: 0, y: -1 }   // up
  ];

  // Filter out directions that would cause immediate collision
  const safeDirections = potentialDirections.filter(dir => {
    const newHead = { x: head.x + dir.x, y: head.y + dir.y };
    return !this.isCollision(newHead);
  });

  // If there are safe directions, prefer moving towards food
  if (safeDirections.length > 0) {
    // Try to prefer direction to food if it's safe
    const foodDir = this.getDirectionTowardsFood(head);

    // Find if the food direction is in safe directions
    const foodDirIndex = safeDirections.findIndex(dir => 
      dir.x === foodDir.x && dir.y === foodDir.y);

    // Choose direction (prefer food direction if safe)
    let chosenDirection;
    if (foodDirIndex !== -1) {
      chosenDirection = safeDirections[foodDirIndex];
    } else {
      // Otherwise pick a random safe direction
      chosenDirection = safeDirections[Math.floor(Math.random() * safeDirections.length)];
    }

    // Update the last direction used
    this.lastDirection = chosenDirection;

    // Move worm head
    head.x += chosenDirection.x;
    head.y += chosenDirection.y;
    this.worm.unshift(head);

    // Check if food is eaten
    if (head.x === this.food.x && head.y === this.food.y) {
      this.score++;
      // Update high score if needed
      if (this.score > this.highScore) {
        this.highScore = this.score;
      }
      // Move food to center
      this.food = {x: Math.floor(this.width/2), y: Math.floor(this.height/2)};
      // Speed up slightly with each food eaten
      this.speed = Math.max(this.speed * 0.95, 100);
    } else {
      // Remove tail if no food was eaten
      this.worm.pop();
    }

    this.consecutiveCollisions = 0;
  } else {
    // No safe moves available
    this.consecutiveCollisions++;
    if (this.consecutiveCollisions >= 3) {
      this.gameOver = true;
    }
  }
}

additionalFunctions.isCollision:
function(position) {
  // Check collision with worm body
  for (let i = 0; i < this.worm.length; i++) {
    if (position.x === this.worm[i].x && position.y === this.worm[i].y) {
      return true;
    }
  }

  // Check collision with walls
  return position.x < 0 || position.x >= this.width || 
         position.y < 0 || position.y >= this.height;
}

additionalFunctions.getDirectionTowardsFood:
function(head) {
  // Get the direction towards the food
  const dx = this.food.x - head.x;
  const dy = this.food.y - head.y;

  // Prioritize the axis with the larger distance
  if (Math.abs(dx) > Math.abs(dy)) {
    return { x: Math.sign(dx), y: 0 };
  } else {
    return { x: 0, y: Math.sign(dy) };
  }
}

additionalFunctions.restart:
function() {
  this.worm = [
    {x: 5, y: Math.floor(this.height/2)},
    {x: 4, y: Math.floor(this.height/2)},
    {x: 3, y: Math.floor(this.height/2)}
  ];

  this.food = {x: Math.floor(this.width/2), y: Math.floor(this.height/2)};
  this.score = 0;
  this.gameOver = false;
  this.consecutiveCollisions = 0;
  this.startTime = Date.now();
  this.timeAlive = 0;
  this.speed = this.gameData.initialSpeed;
  this.lastUpdateTime = 0;
  this.lastDirection = {x: 1, y: 0};
}

additionalFunctions.roundRect:
function(x, y, width, height, radius) {
  if (radius > width / 2) radius = width / 2;
  if (radius > height / 2) radius = height / 2;

  this.ctx.beginPath();
  this.ctx.moveTo(x + radius, y);
  this.ctx.arcTo(x + width, y, x + width, y + height, radius);
  this.ctx.arcTo(x + width, y + height, x, y + height, radius);
  this.ctx.arcTo(x, y + height, x, y, radius);
  this.ctx.arcTo(x, y, x + width, y, radius);
  this.ctx.closePath();
  this.ctx.fill();
}
