initializeCode:
function(display) {
  this.display = display;
  this.canvas = document.createElement('canvas');
  this.canvas.width = 280;
  this.canvas.height = 248;
  display.appendChild(this.canvas);
  this.ctx = this.canvas.getContext('2d');

  this.blockSize = this.gameData.blockSize;
  this.boardWidth = this.gameData.boardWidth;
  this.boardHeight = this.gameData.boardHeight;

  // Calculate offsets to center the game board
  this.boardPixelWidth = this.boardWidth * this.blockSize;
  this.boardPixelHeight = this.boardHeight * this.blockSize;
  this.offsetX = (this.canvas.width - this.boardPixelWidth) / 2;
  this.offsetY = (this.canvas.height - this.boardPixelHeight) / 2;

  this.resetGame();

  // Initialize key handlers for continuous movement
  this.keyStates = {
    left: false,
    right: false,
    down: false
  };

  this.keyRepeatDelay = 100; // ms
  this.lastKeyTime = 0;

  // Start game loop
  this.lastTime = performance.now();
  this.dropCounter = 0;
  this.gameOverAnimationCounter = 0;

  this.render();
}

handleInputCode:
function(button, isPressed) {
  if (this.gameOver) {
    if (isPressed && button === 'a') {
      this.resetGame();
    }
    return;
  }

  if (this.paused) {
    if (isPressed && button === 'b') {
      this.paused = false;
    }
    return;
  }

  if (isPressed && button === 'b') {
    this.paused = true;
    return;
  }

  if (button === 'left') {
    this.keyStates.left = isPressed;
    if (isPressed) {
      this.moveCurrentPiece(-1, 0);
      this.lastKeyTime = performance.now();
    }
  } else if (button === 'right') {
    this.keyStates.right = isPressed;
    if (isPressed) {
      this.moveCurrentPiece(1, 0);
      this.lastKeyTime = performance.now();
    }
  } else if (button === 'down') {
    this.keyStates.down = isPressed;
    if (isPressed) {
      this.moveCurrentPiece(0, 1);
      this.lastKeyTime = performance.now();
      // Award points for manually dropping
      this.score += 1;
    }
  } else if (isPressed && button === 'up') {
    this.rotatePiece();
  } else if (isPressed && button === 'a') {
    this.hardDrop();
  }
}

updateCode:
function(deltaTime) {
  if (this.gameOver || this.paused) {
    return;
  }

  const now = performance.now();

  // Handle continuous key presses with repeat rate
  if (now - this.lastKeyTime > this.keyRepeatDelay) {
    if (this.keyStates.left) {
      this.moveCurrentPiece(-1, 0);
      this.lastKeyTime = now;
    } else if (this.keyStates.right) {
      this.moveCurrentPiece(1, 0);
      this.lastKeyTime = now;
    } else if (this.keyStates.down) {
      this.moveCurrentPiece(0, 1);
      this.lastKeyTime = now;
      // Award points for manually dropping
      this.score += 1;
    }
  }

  // Update drop counter
  this.dropCounter += deltaTime;

  // Get the current level drop speed
  const dropInterval = this.gameData.initialSpeed * Math.pow(this.gameData.speedFactor, this.level - 1);

  // Automatically move piece down based on current level speed
  if (this.dropCounter > dropInterval) {
    this.dropCounter = 0;
    this.moveCurrentPiece(0, 1) || this.lockPiece();
  }

  // Update game over animation if needed
  if (this.gameOver) {
    this.gameOverAnimationCounter += deltaTime;
  }
}

renderCode:
function() {
  // Clear canvas
  this.ctx.fillStyle = '#000000';
  this.ctx.fillRect(0, 0, this.canvas.width, this.canvas.height);

  // Draw background for board
  this.ctx.fillStyle = '#111111';
  this.ctx.fillRect(this.offsetX, this.offsetY, this.boardPixelWidth, this.boardPixelHeight);

  // Draw border around the board
  this.ctx.strokeStyle = '#CCCCCC';
  this.ctx.lineWidth = 1;
  this.ctx.strokeRect(this.offsetX - 1, this.offsetY - 1, this.boardPixelWidth + 2, this.boardPixelHeight + 2);

  // Draw grid lines
  this.ctx.strokeStyle = '#222222';
  this.ctx.lineWidth = 0.5;

  // Vertical grid lines
  for (let x = 1; x < this.boardWidth; x++) {
    this.ctx.beginPath();
    this.ctx.moveTo(this.offsetX + x * this.blockSize, this.offsetY);
    this.ctx.lineTo(this.offsetX + x * this.blockSize, this.offsetY + this.boardPixelHeight);
    this.ctx.stroke();
  }

  // Horizontal grid lines
  for (let y = 1; y < this.boardHeight; y++) {
    this.ctx.beginPath();
    this.ctx.moveTo(this.offsetX, this.offsetY + y * this.blockSize);
    this.ctx.lineTo(this.offsetX + this.boardPixelWidth, this.offsetY + y * this.blockSize);
    this.ctx.stroke();
  }

  // Draw blocks on the board
  this.drawBoard();

  // Draw current piece
  if (!this.gameOver) {
    this.drawPiece(this.currentPiece, this.currentX, this.currentY, this.currentColorIndex);
  }

  // Draw ghost piece (preview of where piece will land)
  if (!this.gameOver && !this.paused) {
    this.drawGhostPiece();
  }

  // Draw next piece preview
  this.drawNextPiece();

  // Draw UI elements
  this.drawUI();

  // Draw pause overlay
  if (this.paused) {
    this.drawPauseScreen();
  }

  // Draw game over overlay
  if (this.gameOver) {
    this.drawGameOverScreen();
  }
}

additionalFunctions.resetGame:
function() {
  // Initialize game board (0 = empty cell)
  this.board = Array(this.boardHeight).fill().map(() => Array(this.boardWidth).fill(0));

  // Initialize game state
  this.score = 0;
  this.lines = 0;
  this.level = this.gameData.initialLevel;
  this.gameOver = false;
  this.paused = false;

  // Spawn first piece
  this.spawnPiece();

  // Create next piece
  this.nextPieceIndex = Math.floor(Math.random() * this.gameData.tetrominos.length);
  this.nextColorIndex = Math.floor(Math.random() * this.gameData.colors.length);
}

additionalFunctions.spawnPiece:
function() {
  // If we have a nextPiece, use it, otherwise create a random one
  if (this.nextPieceIndex !== undefined) {
    this.currentPiece = this.gameData.tetrominos[this.nextPieceIndex];
    this.currentColorIndex = this.nextColorIndex;
  } else {
    this.currentPiece = this.gameData.tetrominos[Math.floor(Math.random() * this.gameData.tetrominos.length)];
    this.currentColorIndex = Math.floor(Math.random() * this.gameData.colors.length);
  }

  // Create the next piece
  this.nextPieceIndex = Math.floor(Math.random() * this.gameData.tetrominos.length);
  this.nextColorIndex = Math.floor(Math.random() * this.gameData.colors.length);

  // Set the starting position (center top)
  this.currentX = Math.floor((this.boardWidth - this.currentPiece[0].length) / 2);
  this.currentY = 0;

  // Check if the new piece overlaps with existing pieces (game over condition)
  if (!this.isValidMove(this.currentPiece, this.currentX, this.currentY)) {
    this.gameOver = true;
  }
}

additionalFunctions.drawBoard:
function() {
  for (let y = 0; y < this.boardHeight; y++) {
    for (let x = 0; x < this.boardWidth; x++) {
      const cell = this.board[y][x];
      if (cell) {
        this.drawBlock(x, y, cell - 1);
      }
    }
  }
}

additionalFunctions.drawPiece:
function(piece, x, y, colorIndex) {
  for (let row = 0; row < piece.length; row++) {
    for (let col = 0; col < piece[row].length; col++) {
      if (piece[row][col]) {
        this.drawBlock(x + col, y + row, colorIndex);
      }
    }
  }
}

additionalFunctions.drawBlock:
function(x, y, colorIndex) {
  const screenX = this.offsetX + x * this.blockSize;
  const screenY = this.offsetY + y * this.blockSize;

  // Draw filled block
  this.ctx.fillStyle = this.gameData.colors[colorIndex];
  this.ctx.fillRect(screenX, screenY, this.blockSize, this.blockSize);

  // Draw highlight (3D effect)
  this.ctx.fillStyle = 'rgba(255, 255, 255, 0.5)';
  this.ctx.fillRect(screenX, screenY, this.blockSize, 2);
  this.ctx.fillRect(screenX, screenY, 2, this.blockSize);

  // Draw shadow
  this.ctx.fillStyle = 'rgba(0, 0, 0, 0.3)';
  this.ctx.fillRect(screenX + this.blockSize - 2, screenY, 2, this.blockSize);
  this.ctx.fillRect(screenX, screenY + this.blockSize - 2, this.blockSize, 2);
}

additionalFunctions.drawGhostBlock:
function(x, y) {
  const screenX = this.offsetX + x * this.blockSize;
  const screenY = this.offsetY + y * this.blockSize;

  // Draw ghost block outline
  this.ctx.strokeStyle = 'rgba(255, 255, 255, 0.3)';
  this.ctx.lineWidth = 1;
  this.ctx.strokeRect(screenX + 1, screenY + 1, this.blockSize - 2, this.blockSize - 2);
}

additionalFunctions.drawGhostPiece:
function() {
  const dropY = this.getDropPosition();

  // Only draw ghost if it's different from current position
  if (dropY > this.currentY) {
    for (let row = 0; row < this.currentPiece.length; row++) {
      for (let col = 0; col < this.currentPiece[row].length; col++) {
        if (this.currentPiece[row][col]) {
          this.drawGhostBlock(this.currentX + col, dropY + row);
        }
      }
    }
  }
}

additionalFunctions.getDropPosition:
function() {
  let testY = this.currentY;

  // Move the piece down until it collides
  while (this.isValidMove(this.currentPiece, this.currentX, testY + 1)) {
    testY++;
  }

  return testY;
}

additionalFunctions.drawNextPiece:
function() {
  const nextPiece = this.gameData.tetrominos[this.nextPieceIndex];
  const pieceWidth = nextPiece[0].length * this.blockSize;
  const pieceHeight = nextPiece.length * this.blockSize;

  // Draw next piece box
  const boxX = this.offsetX + this.boardPixelWidth + 10;
  const boxY = this.offsetY;
  const boxWidth = 60;
  const boxHeight = 60;

  // Draw background
  this.ctx.fillStyle = '#111111';
  this.ctx.fillRect(boxX, boxY, boxWidth, boxHeight);

  // Draw border
  this.ctx.strokeStyle = '#CCCCCC';
  this.ctx.lineWidth = 1;
  this.ctx.strokeRect(boxX, boxY, boxWidth, boxHeight);

  // Draw label
  this.ctx.fillStyle = '#FFFFFF';
  this.ctx.font = '8px "Press Start 2P"';
  this.ctx.textAlign = 'center';
  this.ctx.fillText('NEXT', boxX + boxWidth / 2, boxY - 5);

  // Draw next piece
  const pieceX = boxX + (boxWidth - pieceWidth) / 2;
  const pieceY = boxY + (boxHeight - pieceHeight) / 2;

  for (let row = 0; row < nextPiece.length; row++) {
    for (let col = 0; col < nextPiece[row].length; col++) {
      if (nextPiece[row][col]) {
        const blockX = pieceX + col * this.blockSize;
        const blockY = pieceY + row * this.blockSize;

        // Draw block
        this.ctx.fillStyle = this.gameData.colors[this.nextColorIndex];
        this.ctx.fillRect(blockX, blockY, this.blockSize, this.blockSize);

        // Draw highlight
        this.ctx.fillStyle = 'rgba(255, 255, 255, 0.5)';
        this.ctx.fillRect(blockX, blockY, this.blockSize, 2);
        this.ctx.fillRect(blockX, blockY, 2, this.blockSize);

        // Draw shadow
        this.ctx.fillStyle = 'rgba(0, 0, 0, 0.3)';
        this.ctx.fillRect(blockX + this.blockSize - 2, blockY, 2, this.blockSize);
        this.ctx.fillRect(blockX, blockY + this.blockSize - 2, this.blockSize, 2);
      }
    }
  }
}

additionalFunctions.drawUI:
function() {
  // Draw score, level, and lines
  this.ctx.fillStyle = '#FFFFFF';
  this.ctx.font = '8px "Press Start 2P"';
  this.ctx.textAlign = 'left';

  const textX = this.offsetX - 80;
  let textY = this.offsetY;

  // Draw score
  this.ctx.fillText('SCORE', textX, textY);
  this.ctx.fillText(this.score.toString().padStart(6, '0'), textX, textY + 12);

  // Draw level
  textY += 30;
  this.ctx.fillText('LEVEL', textX, textY);
  this.ctx.fillText(this.level.toString().padStart(2, '0'), textX, textY + 12);

  // Draw lines
  textY += 30;
  this.ctx.fillText('LINES', textX, textY);
  this.ctx.fillText(this.lines.toString().padStart(3, '0'), textX, textY + 12);

  // Draw controls hint
  textY = this.offsetY + this.boardPixelHeight - 60;
  this.ctx.fillText('CONTROLS:', textX, textY);
  this.ctx.fillText('â†â†’â†“: MOVE', textX, textY + 15);
  this.ctx.fillText('â†‘: ROTATE', textX, textY + 30);
  this.ctx.fillText('A: DROP', textX, textY + 45);
  this.ctx.fillText('B: PAUSE', textX, textY + 60);
}

additionalFunctions.drawPauseScreen:
function() {
  // Semi-transparent overlay
  this.ctx.fillStyle = 'rgba(0, 0, 0, 0.7)';
  this.ctx.fillRect(0, 0, this.canvas.width, this.canvas.height);

  // Pause text
  this.ctx.fillStyle = '#FFFFFF';
  this.ctx.font = '16px "Press Start 2P"';
  this.ctx.textAlign = 'center';
  this.ctx.textBaseline = 'middle';
  this.ctx.fillText('PAUSED', this.canvas.width / 2, this.canvas.height / 2);

  // Instructions
  this.ctx.font = '8px "Press Start 2P"';
  this.ctx.fillText('PRESS B TO RESUME', this.canvas.width / 2, this.canvas.height / 2 + 30);
}

additionalFunctions.drawGameOverScreen:
function() {
  // Semi-transparent overlay with animation
  const rows = Math.min(this.boardHeight, Math.floor(this.gameOverAnimationCounter / 50));

  // Draw overlay one row at a time for animation effect
  this.ctx.fillStyle = 'rgba(255, 0, 0, 0.3)';
  for (let y = 0; y < rows; y++) {
    this.ctx.fillRect(
      this.offsetX,
      this.offsetY + y * this.blockSize,
      this.boardPixelWidth,
      this.blockSize
    );
  }

  // Only show game over text after animation finishes
  if (rows >= this.boardHeight) {
    // Game over text
    this.ctx.fillStyle = '#FFFFFF';
    this.ctx.font = '16px "Press Start 2P"';
    this.ctx.textAlign = 'center';
    this.ctx.textBaseline = 'middle';
    this.ctx.fillText('GAME OVER', this.canvas.width / 2, this.canvas.height / 2 - 20);

    // Final score
    this.ctx.font = '8px "Press Start 2P"';
    this.ctx.fillText(`SCORE: ${this.score}`, this.canvas.width / 2, this.canvas.height / 2 + 10);

    // Restart instructions
    this.ctx.fillText('PRESS A TO RESTART', this.canvas.width / 2, this.canvas.height / 2 + 30);
  }
}

additionalFunctions.moveCurrentPiece:
function(dx, dy) {
  if (this.isValidMove(this.currentPiece, this.currentX + dx, this.currentY + dy)) {
    this.currentX += dx;
    this.currentY += dy;
    return true;
  }
  return false;
}

additionalFunctions.rotatePiece:
function() {
  const rotated = this.rotateMatrix(this.currentPiece);
  if (this.isValidMove(rotated, this.currentX, this.currentY)) {
    this.currentPiece = rotated;
  } else {
    // Try wall kicks (offset rotated piece to find a valid position)
    const kicks = [-1, 1, -2, 2];
    for (let kick of kicks) {
      if (this.isValidMove(rotated, this.currentX + kick, this.currentY)) {
        this.currentPiece = rotated;
        this.currentX += kick;
        break;
      }
    }
  }
}

additionalFunctions.rotateMatrix:
function(matrix) {
  const N = matrix.length;
  const result = [];

  // Create a new rotated matrix
  for (let i = 0; i < matrix[0].length; i++) {
    result[i] = [];
    for (let j = 0; j < N; j++) {
      result[i][j] = matrix[N - 1 - j][i];
    }
  }

  return result;
}

additionalFunctions.isValidMove:
function(piece, x, y) {
  for (let row = 0; row < piece.length; row++) {
    for (let col = 0; col < piece[row].length; col++) {
      if (piece[row][col]) {
        const newX = x + col;
        const newY = y + row;

        // Check bounds
        if (newX < 0 || newX >= this.boardWidth || newY < 0 || newY >= this.boardHeight) {
          return false;
        }

        // Check collision with existing blocks
        if (newY >= 0 && this.board[newY][newX]) {
          return false;
        }
      }
    }
  }
  return true;
}

additionalFunctions.hardDrop:
function() {
  const startY = this.currentY;
  while (this.moveCurrentPiece(0, 1)) {
    // Keep moving down until collision
  }

  // Award points based on the distance dropped
  this.score += 2 * (this.currentY - startY);

  // Lock the piece immediately
  this.lockPiece();
}

additionalFunctions.lockPiece:
function() {
  // Add current piece to the board
  for (let row = 0; row < this.currentPiece.length; row++) {
    for (let col = 0; col < this.currentPiece[row].length; col++) {
      if (this.currentPiece[row][col]) {
        const boardY = this.currentY + row;
        const boardX = this.currentX + col;
        if (boardY >= 0) {
          this.board[boardY][boardX] = this.currentColorIndex + 1; // +1 because 0 is empty
        }
      }
    }
  }

  // Check for completed lines
  this.checkLines();

  // Spawn a new piece
  this.spawnPiece();
}

additionalFunctions.checkLines:
function() {
  let linesCleared = 0;

  for (let y = 0; y < this.boardHeight; y++) {
    // Check if this line is full
    const isLineFull = this.board[y].every(cell => cell !== 0);

    if (isLineFull) {
      // Remove this line
      this.board.splice(y, 1);
      // Add empty line at the top
      this.board.unshift(Array(this.boardWidth).fill(0));
      // Increment counters
      linesCleared++;
      this.lines++;
    }
  }

  // Award points based on number of lines cleared at once
  if (linesCleared > 0) {
    // Scoring: 100 * level for 1 line, 300 * level for 2, 500 for 3, 800 for 4 (Tetris)
    const pointValues = [0, 100, 300, 500, 800];
    this.score += pointValues[linesCleared] * this.level;

    // Level up every this.gameData.levelUpLines lines
    if (Math.floor(this.lines / this.gameData.levelUpLines) + 1 > this.level) {
      this.level = Math.floor(this.lines / this.gameData.levelUpLines) + 1;
    }
  }
}
